package Basics;

public class Kleines11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//
		// Kleines1x1
		for (int i = 1; i < 11; i++) {
			for (int x = 1; x < 11; x++) {
				//
				System.out.print(i * x); 
				System.out.print("\t");

			}
				System.out.println();

		}
	}
}
